        //Check if origin token is the one attacked
        if(effectOriginToken.id === workflow.targets.first().id) return;

        //Check if target also has sentinel
        if(target.actor.items.find(i => i.name.toLowerCase() === "sentinel")) return;
    }

    if(dialogTitle === "Riposte Attack") {
        //Check if origin token is the one attacked
        if(effectOriginToken.id !== workflow.targets.first().id) return;
        if(hasRiposteCpr) {
            riposteItem = hasRiposteCpr;
        }
        else if(hasRiposte) {
            riposteItem = hasRiposte;
        }
        const superiorityNames = ["superiority dice", "superiority die"];

        let resourceExistsWithValue = [effectOriginToken.actor.system.resources.primary, effectOriginToken.actor.system.resources.secondary, effectOriginToken.actor.system.resources.tertiary].some(resource =>
            superiorityNames.includes(resource?.label.toLowerCase()) && resource.value !== 0);
        let itemExistsWithValue;

        if (!resourceExistsWithValue) {
            itemExistsWithValue = !!effectOriginToken.actor.items.find(i => superiorityNames.includes(i.name.toLowerCase()) && i.system.uses.value !== 0);
        }

        if (!resourceExistsWithValue && !itemExistsWithValue) return;

        let dialogContentRiposte = `
            <div style='display: flex; align-items: center; justify-content: space-between;'>
                <div style='flex: 1;'>
                            Use your Riposte Maneuver for an Opportunity Attack?
                </div>
                <div style='border-left: 1px solid #ccc; padding-left: 10px; text-align: center;'>
                    <p><b>Time remaining</b></p>
                    <p><span id='countdown' style='font-size: 16px; color: red;'>${timeLeft}</span> seconds</p>
                </div>
            </div>`;

        // Create temporary effect for dialog
        const tempEffectDataRiposte = [{
            "icon": "icons/skills/melee/weapons-crossed-swords-yellow.webp",
            "name": "Maneuvers: Riposte Opportunity Attack",
                "changes": [],
                "origin": effectOriginToken.actor.uuid,
                "disabled": false,
                "duration": {
                    "rounds": 1
                },
                "flags": {
                    "dae": {
                        "macroRepeat": "none"
                    },
                    "effectmacro": {
                        "onCreate": {
                            "script": `
                                let dialog = new Dialog({
                                    title: "Riposte Maneuver",
                                    content: \`${dialogContentRiposte}\`,
                                    buttons: {
                                        yes: {
                                            label: "Yes",
                                            callback: async (html) => {
                                                // Logic for yes response
                                                let riposteItem = await fromUuid(\`${riposteItem.uuid}\`)
                                                await riposteItem.use();
                                                await actor.setFlag("midi-qol", "checkRiposteDecision", true);
                                            }
                                        },
                                        no: {
                                            label: "No",
                                            callback: async () => {
                                                await actor.setFlag("midi-qol", "checkRiposteDecision", false);
                                            }
                                        },
                                    }, default: "no",
                                        render: (html) => { let timeLeft = ${timeLeft}; const countdownElement = html.find("#countdown"); const timer = setInterval(() => { timeLeft--; countdownElement.text(timeLeft); if (timeLeft <= 0) { clearInterval(timer); dialog.close(); } }, 1000); setTimeout(() => { clearInterval(timer); if (timeLeft > 0) dialog.close(); }, timeLeft * 1000); } }); dialog.render(true);
                            `
                        }
                    }
                }
            }];

        await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: effectOriginToken.actor.uuid, effects: tempEffectDataRiposte });
        await wait(100);
        const tempEffectRemoveRiposte = effectOriginToken.actor.effects.getName("Maneuvers: Riposte Opportunity Attack");
        await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: effectOriginToken.actor.uuid, effects: [tempEffectRemoveRiposte.id] });

        while (riposteDecision === undefined) {
            riposteDecision = await effectOriginToken.actor.getFlag("midi-qol", "checkRiposteDecision");
            if(riposteDecision === undefined) {
                await new Promise(resolve => setTimeout(resolve, 1000));
            }
        }

        await effectOriginToken.actor.unsetFlag("midi-qol", "checkRiposteDecision");
        if (riposteDecision === false) return;
    }

    // Check valid weapons
    let validWeapons = effectOriginToken.actor.items.filter(item => {
        return (item.system.actionType === "mwak" && item.system.equipped === true);
    });
    if (!validWeapons.length) return;
    // Find 'Unarmed Strike' from the validWeapons array and add to end of list
    const unarmedIndex = validWeapons.findIndex(item => item.name.toLowerCase() === "unarmed strike");
    let unarmedStrike;
    if (unarmedIndex > -1) {
        unarmedStrike = validWeapons.splice(unarmedIndex, 1)[0];
        validWeapons.push(unarmedStrike);
    }

    let optionData = validWeapons.map(item => `<option value="${item.uuid}">${item.name}</option>`).join("");
    let dialogContent = `
        <div style='display: flex; align-items: center; justify-content: space-between;'>
            <div style='flex: 1;'>
                Would you like to use your reaction to attack?<br/><br/>
                Choose your Weapon: <select id='item-select'>${optionData}</select>
            </div>
            <div style='border-left: 1px solid #ccc; padding-left: 10px; text-align: center;'>
                <p><b>Time remaining</b></p>
                <p><span id='countdown' style='font-size: 16px; color: red;'>${timeLeft}</span> seconds</p>
            </div>
        </div>`;

    // Assign player actor for socket, default to GM if no player active for the actor
    let browserUser = MidiQOL.playerForActor(effectOriginToken.actor);
    if (!browserUser?.active) {
        browserUser = game.users?.activeGM;
    }

// Create temporary item for dialog
const tempItemData = {
    "name": "Sentinel Reaction",
    "type": "feat",
    "img": "icons/magic/symbols/runes-etched-steel-blade.webp",
    "effects": [{
		"icon": "icons/magic/symbols/runes-etched-steel-blade.webp",
		"name": "Sentinel Reaction",
        "changes": [],
        "origin": effectOriginToken.actor.uuid,
        "disabled": false,
        "duration": {
            "rounds": 1
        },
        "flags": {
            "dae": {
                "macroRepeat": "none"
            },
            "effectmacro": {
                "onCreate": {
                    "script": `
                        let dialog = new Dialog({
                            title: "${dialogTitle}",
                            content: \`${dialogContent}\`,
                            buttons: {
                                yes: {
                                    label: "Yes",
                                    callback: async (html) => {
                                        // Logic for yes response
                                        let selectedItemUuid = html.find("#item-select").val();
                                        if (!selectedItemUuid) {
                                            console.log("No weapon selected");
                                            return;
                                        }

